declare module '*.scss' {
  let content: any;
  // noinspection JSUnusedGlobalSymbols
  export default content;
}

declare module '*.css' {
  let content: Map<string, string>;
  // noinspection JSUnusedGlobalSymbols
  export default content;
}

declare module '*.json' {
  let content: any;
  // noinspection JSUnusedGlobalSymbols
  export default content;
}
